function findTheSentence(string) {
  const rows = string.split("+");
  const code = [];
  for (let i = 0; i < rows.length; i++) {
    for (let j = 0; j < rows[i].length; j++) {
      if (rows[i][j] !== "|") {
        code[j] = rows[i][j];
      }
    }
  }
  return code.join("");
}

